import React, { useState, useRef } from "react";
import "./SignUp.css";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";

const SignUp = () => {
  const [signupForm, setSignupForm] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [loginForm, setLoginForm] = useState({
    email: "",
    password: "",
  });

  const [signupError, setSignupError] = useState("");
  const [loginError, setLoginError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const [isLogin, setIsLogin] = useState(false); // Track if we are in the login or signup form
  const checkboxRef = useRef();

  const auth = getAuth();

  // Handle input changes for both sign-up and login forms
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (isLogin) {
      setLoginForm((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    } else {
      setSignupForm((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  // Handle Sign-up
  const handleSignUp = async (e) => {
    e.preventDefault();
    const { email, password } = signupForm;

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("User created:", user);
      setSuccessMessage("Signup successful! Redirecting to login...");
      setSignupError(""); // Clear previous error messages
      setSignupForm({
        username: "",
        email: "",
        password: "",
      });

      // Switch to login after a short delay
      setTimeout(() => {
        setIsLogin(true);
        setSuccessMessage(""); // Clear success message
      }, 1500);
    } catch (error) {
      console.error("Error during signup:", error.message);
      setSignupError(error.message);
      setSuccessMessage(""); // Clear any success messages
    }
  };

  // Handle Login
  const handleLogin = async (e) => {
    e.preventDefault();
    const { email, password } = loginForm;

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("User logged in:", user);
      setSuccessMessage("Login successful!");
      setLoginError(""); // Clear previous login errors
      setLoginForm({
        email: "",
        password: "",
      });
    } catch (error) {
      console.error("Error during login:", error.message);
      setLoginError(error.message);
      setSuccessMessage(""); // Clear success message if login fails
    }
  };

  return (
    <div className="main">
      <input type="checkbox" id="chk" ref={checkboxRef} aria-hidden="true" />
      <div className="form-container">
        {/* Toggle between Sign Up and Login */}
        {isLogin ? (
          // Login Form
          <div className="login">
            <form onSubmit={handleLogin}>
              <label htmlFor="chk" aria-hidden="true">
                Login
              </label>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={loginForm.email}
                onChange={handleInputChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={loginForm.password}
                onChange={handleInputChange}
                required
              />
              <button type="submit">Login</button>
            </form>
            {successMessage && <p className="success-message">{successMessage}</p>}
            {loginError && <p className="error-message">{loginError}</p>}
            <p>
              Don't have an account?{" "}
              <span onClick={() => { setIsLogin(false); setSignupError(""); }}>Sign up</span>
            </p>
          </div>
        ) : (
          // Sign Up Form
          <div className="signup">
            <form onSubmit={handleSignUp}>
              <label htmlFor="chk" aria-hidden="true">
                Sign up
              </label>
              <input
                type="text"
                name="username"
                placeholder="User name"
                value={signupForm.username}
                onChange={handleInputChange}
                required
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={signupForm.email}
                onChange={handleInputChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={signupForm.password}
                onChange={handleInputChange}
                required
              />
              <button type="submit">Sign up</button>
            </form>
            {successMessage && <p className="success-message">{successMessage}</p>}
            {signupError && <p className="error-message">{signupError}</p>}
            <p>
              Already have an account?{" "}
              <span onClick={() => { setIsLogin(true); setSignupError(""); }}>Login</span>
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default SignUp;
