import React, { useState } from "react";
import "./SignUp.css";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";

const SignUp = () => {
  const [isSignUp, setIsSignUp] = useState(true); // State to toggle between Sign Up and Login
  const [signupForm, setSignupForm] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [signupError, setSignupError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const auth = getAuth();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSignupForm((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    const { email, password } = signupForm;

    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        email,
        password
      );
      const user = userCredential.user;
      console.log("User created:", user);
      setSuccessMessage("Signup successful! Redirecting to login...");
      setSignupError(""); // Clear any previous error messages
      setSignupForm({
        username: "",
        email: "",
        password: "",
      });

      // Redirect to login after a short delay
      setTimeout(() => {
        setIsSignUp(false); // Switch to the login form
        setSuccessMessage(""); // Clear success message
      }, 1500);
    } catch (error) {
      console.error("Error during signup:", error.message);
      setSignupError(error.message);
      setSuccessMessage(""); // Clear any success messages
    }
  };

  return (
    <div className="main">
      {/* Conditionally render based on `isSignUp` state */}
      {isSignUp ? (
        <div className="signup">
          <form onSubmit={handleSignUp}>
            <h2>Sign Up</h2>
            <input
              type="text"
              name="username"
              placeholder="User name"
              value={signupForm.username}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={signupForm.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={signupForm.password}
              onChange={handleInputChange}
              required
            />
            <button type="submit">Sign up</button>
          </form>
          {successMessage && <p className="success-message">{successMessage}</p>}
          {signupError && <p className="error-message">{signupError}</p>}
          <p>
            Already have an account?{" "}
            <span className="toggle-link" onClick={() => setIsSignUp(false)}>
              Login here
            </span>
          </p>
        </div>
      ) : (
        <div className="login">
          <form>
            <h2>Login</h2>
            <input type="email" name="email" placeholder="Email" required />
            <input type="password" name="pswd" placeholder="Password" required />
            <button>Login</button>
          </form>
          <p>
            Don't have an account?{" "}
            <span className="toggle-link" onClick={() => setIsSignUp(true)}>
              Sign up here
            </span>
          </p>
        </div>
      )}
    </div>
  );
};

export default SignUp;
