import React, { useState } from "react";
import "./SignUp.css"; // Add your styles here

const SignUp = () => {
  // State to track which form is visible: 'login' or 'signup'
  const [isLogin, setIsLogin] = useState(true); 

  // Toggle functions to show/hide login or signup form
  const toggleSignup = () => {
    setIsLogin(false); // Show signup form
  };

  const toggleLogin = () => {
    setIsLogin(true); // Show login form
  };

  return (
    <div className="form-modal">
      {/* Form toggle buttons */}
      <div className="form-toggle">
        <button
          id="login-toggle"
          onClick={toggleLogin}
          style={{
            backgroundColor: isLogin ? "#57B846" : "#fff",
            color: isLogin ? "#fff" : "#222",
          }}
        >
          Log in
        </button>
        <button
          id="signup-toggle"
          onClick={toggleSignup}
          style={{
            backgroundColor: !isLogin ? "#57B846" : "#fff",
            color: !isLogin ? "#fff" : "#222",
          }}
        >
          Sign up
        </button>
      </div>

      {/* Login Form */}
      {isLogin && (
        <div id="login-form">
          <form>
            <input type="text" placeholder="Enter email or username" />
            <input type="password" placeholder="Enter password" />
            <button type="button" className="btn login">
              Login
            </button>
            <p>
              <a href="javascript:void(0)">Forgotten account</a>
            </p>
            <hr />
          </form>
        </div>
      )}

      {/* Sign-up Form */}
      {!isLogin && (
        <div id="signup-form">
          <form>
            <input type="email" placeholder="Enter your email" />
            <input type="text" placeholder="Choose username" />
            <input type="password" placeholder="Create password" />
            <button type="button" className="btn signup">
              Create account
            </button>
            <p>
              Clicking <strong>create account</strong> means that you agree to our{" "}
              <a href="javascript:void(0)">terms of services</a>.
            </p>
            <hr />
          </form>
        </div>
      )}
    </div>
  );
};

export default SignUp;
