import './App.css';
import ManagerDashboard from './component/Dashboard/ManagerDashboard/ManagerDashboard';
import SignUp from './component/SignUp/SignUp';
import { app } from './firebase';
import { getAuth } from 'firebase/auth';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';


const auth = getAuth(app);

function App() {

  return (
    <Router> {/* Wrap the whole app with Router */}
      <div className="App">
        <Routes>
          {/* Route for the sign-up page */}
          <Route path="/" element={<SignUp />} />

          {/* Route for Project Manager Dashboard */}
          <Route path="/project-manager-dashboard" element={<ManagerDashboard />} />

          {/* Optionally add more routes here */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
