import React, { useState } from "react";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import { app } from "../../firebase"; // Ensure your firebase.js is correctly imported
import { getDatabase, ref, set } from "firebase/database"; // Import Firebase Realtime Database functions
import { useHistory } from "react-router-dom"; // Use for redirection

import "./SignUp.css"; // Add your styles here

const SignUp = () => {
  // State to manage the panel view
  const [isRightPanelActive, setIsRightPanelActive] = useState(false);

  // State for form inputs
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState(""); // for sign-up name input
  const [role, setRole] = useState("employee"); // New state for user role
  const [error, setError] = useState(""); // For handling errors
  const [isLoading, setIsLoading] = useState(false); // To show loading indicator

  const auth = getAuth(app); // Firebase Auth instance
  const database = getDatabase(app); // Firebase Realtime Database instance
  const history = useHistory(); // Used for redirection

  // Handle SignUp
  const handleSignUpClick = async () => {
    setIsLoading(true);
    setError(""); // Clear previous errors

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("User signed up:", user);

      // Save the user's data, including role, in the Realtime Database
      const userRef = ref(database, 'users/' + user.uid); // Reference to the user's node in the database
      await set(userRef, {
        username: name,
        email: email,
        uid: user.uid,
        role: role, // Save the selected role
      });

      console.log("User data saved to database");

      // Navigate to the respective dashboard based on the role
      if (role === "project_manager") {
        history.push("/project-manager-dashboard");
      } else {
        history.push("/employee-dashboard");
      }

      setIsRightPanelActive(true); // Switch to sign-in panel after successful sign-up
    } catch (error) {
      setError(error.message);
      console.error("Error signing up:", error.message);
    }
    setIsLoading(false);
  };

  // Handle SignIn
  const handleSignInClick = async () => {
    setIsLoading(true);
    setError(""); // Clear previous errors
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("User signed in:", user);
      // You can redirect or perform other actions after successful sign-in
    } catch (error) {
      setError(error.message);
      console.error("Error signing in:", error.message);
    }
    setIsLoading(false);
  };

  return (
    <div className={`container ${isRightPanelActive ? "right-panel-active" : ""}`} id="container">
      {/* Sign Up Form */}
      <div className="form-container sign-up-container">
        <form onSubmit={(e) => e.preventDefault()}>
          <h1>Sign Up</h1>
          <input 
            type="text" 
            placeholder="Name" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required 
          />
          <input 
            type="email" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
          <input 
            type="password" 
            placeholder="Password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />

          {/* Role Selection */}
          <select 
            value={role} 
            onChange={(e) => setRole(e.target.value)} 
            required
          >
            <option value="employee">Employee</option>
            <option value="project_manager">Project Manager</option>
          </select>

          <button onClick={handleSignUpClick} disabled={isLoading}>
            {isLoading ? "Signing Up..." : "Sign Up"}
          </button>
          {error && <p style={{ color: "red" }}>{error}</p>}
        </form>
      </div>

      {/* Sign In Form */}
      <div className="form-container sign-in-container">
        <form onSubmit={(e) => e.preventDefault()}>
          <h1>Sign In</h1>
          <input 
            type="email" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
          <input 
            type="password" 
            placeholder="Password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />
          <a href="#">Forgot your password?</a>
          <button onClick={handleSignInClick} disabled={isLoading}>
            {isLoading ? "Signing In..." : "Sign In"}
          </button>
          {error && <p style={{ color: "red" }}>{error}</p>}
        </form>
      </div>

      {/* Overlay Container */}
      <div className="overlay-container">
        <div className="overlay">
          <div className="overlay-panel overlay-left">
            <h1>Welcome Back!</h1>
            <p>To stay connected with us please login with your personal info</p>
            <button className="ghost" onClick={() => setIsRightPanelActive(false)}>
              Sign In
            </button>
          </div>
          <div className="overlay-panel overlay-right">
            <h1>Hello, Friend!</h1>
            <p>Enter your personal details and start your journey with us</p>
            <button className="ghost" onClick={() => setIsRightPanelActive(true)}>
              Sign Up
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
