import React, { useState } from "react";
import "./SignUp.css";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";

const SignUp = () => {
  const [signupForm, setSignupForm] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [loginForm, setLoginForm] = useState({
    email: "",
    password: "",
  });

  const [signupError, setSignupError] = useState("");
  const [loginError, setLoginError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [isLogin, setIsLogin] = useState(false); // Track whether the user is in login or signup form

  const auth = getAuth();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (isLogin) {
      setLoginForm((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    } else {
      setSignupForm((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  // Handle Sign-up
  const handleSignUp = async (e) => {
    e.preventDefault();
    const { email, password } = signupForm;

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("User created:", user);
      setSuccessMessage("Signup successful! Welcome aboard.");
      setSignupError(""); // Clear previous error messages
      setSignupForm({
        username: "",
        email: "",
        password: "",
      });
      setTimeout(() => {
        document.getElementById("chk").checked = false; // Switch to the login form
        setSuccessMessage(""); // Clear success message
      }, 1500);
    } catch (error) {
      console.error("Error during signup:", error.message);
      setSignupError(error.message);
      setSuccessMessage(""); // Clear any success messages
    }
  };

  // Handle Login
  const handleLogin = async (e) => {
    e.preventDefault();
    const { email, password } = loginForm;

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("User logged in:", user);
      setSuccessMessage("Login successful!");
      setLoginError(""); // Clear previous login errors
      setLoginForm({
        email: "",
        password: "",
      });
    } catch (error) {
      console.error("Error during login:", error.message);
      setLoginError(error.message);
      setSuccessMessage(""); // Clear success message if login fails
    }
  };

  return (
    <div className="main">
      <input type="checkbox" id="chk" aria-hidden="true" />

      {/* Sign Up Section */}
      {!isLogin ? (
        <div className="signup">
          <form onSubmit={handleSignUp}>
            <label htmlFor="chk" aria-hidden="true">
              Sign up
            </label>
            <input
              type="text"
              name="username"
              placeholder="User name"
              value={signupForm.username}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={signupForm.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={signupForm.password}
              onChange={handleInputChange}
              required
            />
            <button type="submit">Sign up</button>
          </form>
          {successMessage && <p className="success-message">{successMessage}</p>}
          {signupError && <p className="error-message">{signupError}</p>}
        </div>
      ) : (
        // Login Section
        <div className="login">
          <form onSubmit={handleLogin}>
            <label htmlFor="chk" aria-hidden="true">
              Login
            </label>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={loginForm.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={loginForm.password}
              onChange={handleInputChange}
              required
            />
            <button type="submit">Login</button>
          </form>
          {successMessage && <p className="success-message">{successMessage}</p>}
          {loginError && <p className="error-message">{loginError}</p>}
        </div>
      )}

      <div className="switch-form">
        {/* Toggle between Sign up and Login */}
        {!isLogin ? (
          <p>
            Already have an account?{" "}
            <span onClick={() => setIsLogin(true)}>Login</span>
          </p>
        ) : (
          <p>
            Don't have an account?{" "}
            <span onClick={() => setIsLogin(false)}>Sign up</span>
          </p>
        )}
      </div>
    </div>
  );
};

export default SignUp;
