// src/components/EmployeeDashboard/EmployeeDashboard.jsx

import React, { useState, useEffect } from "react";
import { getTasksForProject } from "../../../firestoreService";
import { getAuth, signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";

function EmployeeDashboard({ projectId }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate(); // To navigate after logging out

  // Fetch tasks for a specific project
  useEffect(() => {
    const fetchTasks = async () => {
      setLoading(true);
      try {
        const fetchedTasks = await getTasksForProject(projectId);
        setTasks(fetchedTasks); // Store tasks in state
      } catch (e) {
        console.error("Error fetching tasks: ", e);
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, [projectId]); // Depend on projectId so tasks are fetched whenever projectId changes

  // Logout logic
  const handleLogout = async () => {
    try {
      await signOut(getAuth()); // Sign out the user
      console.log("User logged out");
      navigate("/"); // Redirect to the login page after logout
    } catch (e) {
      console.error("Error logging out: ", e);
    }
  };

  return (
    <div>
      <h1>Employee Dashboard</h1>
      {loading && <p>Loading tasks...</p>}
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <strong>{task.name}</strong>: {task.description} (Status: {task.status})
          </li>
        ))}
      </ul>
      {/* Logout Button */}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default EmployeeDashboard;
