import React, { useState, useRef } from "react";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { setDoc, doc } from "firebase/firestore";
import { db} from '../../firebase'

const SignUp = () => {
  const [signupForm, setSignupForm] = useState({
    username: "",
    email: "",
    password: "",
    role: "Employee", // Default role is Employee
  });

  const [signupError, setSignupError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const checkboxRef = useRef();
  const auth = getAuth();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSignupForm((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    const { email, password, username, role } = signupForm;

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Save user data and role to Firestore
      await setDoc(doc(db, "users", user.uid), {
        username,
        email,
        role,
      });

      console.log("User created:", user);
      setSuccessMessage("Signup successful! Redirecting to login...");
      setSignupError(""); // Clear previous error messages
      setSignupForm({
        username: "",
        email: "",
        password: "",
        role: "Employee", // Reset role
      });

      setTimeout(() => {
        if (checkboxRef.current) {
          checkboxRef.current.checked = false; // Switch to the login form
        }
        setSuccessMessage(""); // Clear success message
      }, 1500);
    } catch (error) {
      console.error("Error during signup:", error.message);
      setSignupError(error.message);
      setSuccessMessage(""); // Clear any success messages
    }
  };

  return (
    <div className="main">
      <input type="checkbox" id="chk" ref={checkboxRef} aria-hidden="true" />

      <div className="signup">
        <form onSubmit={handleSignUp}>
          <label htmlFor="chk" aria-hidden="true">
            Sign up
          </label>
          <input
            type="text"
            name="username"
            placeholder="User name"
            value={signupForm.username}
            onChange={handleInputChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={signupForm.email}
            onChange={handleInputChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={signupForm.password}
            onChange={handleInputChange}
            required
          />
          <select
            name="role"
            value={signupForm.role}
            onChange={handleInputChange}
            required
          >
            <option value="Employee">Employee</option>
            <option value="Project Manager">Project Manager</option>
          </select>
          <button type="submit">Sign up</button>
        </form>
        {successMessage && <p className="success-message">{successMessage}</p>}
        {signupError && <p className="error-message">{signupError}</p>}
      </div>

      <div className="login">
        <form>
          <label htmlFor="chk" aria-hidden="true">
            Login
          </label>
          <input type="email" name="email" placeholder="Email" required />
          <input type="password" name="pswd" placeholder="Password" required />
          <button>Login</button>
        </form>
      </div>
    </div>
  );
};

export default SignUp;
