// src/components/EmployeeDashboard/EmployeeDashboard.jsx

import React, { useState, useEffect } from "react";
import { getTasksForProject } from "../../firebase/firestoreService"; // Import service functions

function EmployeeDashboard({ projectId }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch tasks for a specific project
  useEffect(() => {
    const fetchTasks = async () => {
      setLoading(true);
      try {
        const fetchedTasks = await getTasksForProject(projectId);
        setTasks(fetchedTasks); // Store tasks in state
      } catch (e) {
        console.error("Error fetching tasks: ", e);
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, [projectId]); // Depend on projectId so tasks are fetched whenever projectId changes

  return (
    <div>
      <h1>Employee Dashboard</h1>
      {loading && <p>Loading tasks...</p>}
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <strong>{task.name}</strong>: {task.description} (Status: {task.status})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EmployeeDashboard;
